const initialState = {
    pokemons:[],
    allTypes: []
    
};


function rootReducer(state=initialState,action){
    switch (action.type) {
        case 'GET_POKEMONS':
            return{
                ...state,
                pokemons: action.payload
            }

        case'FILTER_TYPES':
            const allTypes = state.allTypes;
        const typesFilter= action.payload === 'All'?allTypes: allTypes.filter(e=> e.type.name.includes(action.payload)   );
        return{
            ...state,
            pokemons: typesFilter
        }
            
    
            default: return state;
    }

}

export default rootReducer;
