import React from 'react';
import styles from '../components/styles/pagination.module.css'

export  function Pagination({recipesPerPage,allRecipes,pagination}){
const pageNumber= []; 

for (let i = 0; i <= Math.ceil(allRecipes/recipesPerPage)-1 ; i++) {
    pageNumber.push(i+1);
}
return(
    <nav className={styles.nav}>
        <ul className={styles.ul}>
            {pageNumber &&
            pageNumber.map(e =>(
                <li className={styles.li} key={e}>
                    <a href onClick ={()=> pagination(e)}   > {e}</a>
                </li>
            ))

            }

        </ul>
    </nav>
)

} ;
