                <input type="text" value={input.img} name="img" onChange={hadleChange}></input>
            </div>
            <div>
                <labels>speed</labels>
                <input type="number" value={input.speed} name="speed" onChange={hadleChange}></input>
            </div>
            <div>
                <labels>height</labels>
                <input type="number" value={input.height} name="height" onChange={hadleChange}></input>
            </div>
            <div>
                <labels>weight</labels>
                <input type="number" value={input.weight} name="weight" onChange={hadleChange}></input>
            </div>
            {Types?.map( (e) => {
                return( 
            <div>
                <labels>{e.name}</labels>
                <input type="checkbox" value={e.name} name={e.name} onChange= {hadleCheck(e)}></input>
            </div> )
}) }


        </form>

    </div>
)
}
