import React from "react";
import {useState,useEffect} from "react";
import { Link,useHistory } from "react-router-dom";
import { PostPokemonCreate,GetTypes } from "../actions";
import { useDispatch,useSelector } from "react-redux";

export default function PokemonCreate(){
    const dispatch= useDispatch();
    const Types = useSelector((state)=> state.allTypes) ;
    const [input,setInput] = useState (
                                        {                                
                                            name:"",
                                            hp:"",
                                            attack:"",
                                            defense:"",
                                            img:"",
                                            speed:"",
                                            height:"",
                                            weight:"",
                                            type:[]
                                        });



useEffect(()=> {
    dispatch(GetTypes())
},[dispatch]);

function hadleChange(e) {
    setInput({
        ...input,
        [e.target.name] : e.target.value
    })
}
function hadleCheck(e) {
    console.log(input.type);
    var type=e.target.name;
    if (e.target.checked && !input.type.includes(type)) {
      
        setInput({
            ...input,
            type: input.type.concat(type)
        })
    }
}
function HandleNoCheck(e) {
    var type=e.target.name;
    if(!e.target.checked ===true && input.type.includes(type)){ 
        var typeDelete= input.type.filter(e=> !type)
         setInput({
             ...input,
             type: typeDelete
         })
     } 
}



      
        
    
    



return(
    <div>
        <Link to='/pokemons'><button> Return </button></Link>
        <h1>create your pokemon</h1>
        <form>
            <div>
                <label>Nombre</label>
                <input type="text" value={input.name} name="name" onChange={hadleChange}></input>
            </div>
            <div>
                <label>Hp</label>
                <input type="number" value={input.hp} name="hp" onChange={hadleChange}></input>
            </div>
            <div>
                <label>Attack</label>
                <input type="number" value={input.attack} name="attack" onChange={hadleChange}></input>
            </div>
            <div>
                <label>Defense</label>
                <input type="number" value={input.defense} name="defense" onChange={hadleChange}></input>
            </div>
            <div>
                <label>img</label>
                <input type="text" value={input.img} name="img" onChange={hadleChange}></input>
            </div>
            <div>
                <label>speed</label>
                <input type="number" value={input.speed} name="speed" onChange={hadleChange}></input>
            </div>
            <div>
                <label>height</label>
                <input type="number" value={input.height} name="height" onChange={hadleChange}></input>
            </div>
            <div>
                <label>weight</label>
                <input type="number" value={input.weight} name="weight" onChange={hadleChange}></input>
            </div>
            {Types?.map( (e) => {
                return( 
            <div>
                <label>{e.name}</label>
                <input type="checkbox"  name={e.name}  onClick={(e)=>hadleCheck(e)} onChange={(e)=>HandleNoCheck(e)} key={e.id+"qwe" }></input>
            </div> )
}) }


        </form>

    </div>
)
}
