import Card from './Card';


export default function Home() {
    const dispatch = useDispatch();
    const allPokemons = useSelector ((state)=> state.pokemons)

    useEffect(()=>{ dispatch(GetPokemons());
    },[dispatch])

    function handleClick(e){
        e.preventDefault();
        dispatch(GetPokemons)
    }

        
    

