const initialState = {
    pokemons:[],
    allTypes:[]
};


function rootReducer(state=initialState,action){
    switch (action.type) {
        case 'GET_POKEMONS':
            return{
                ...state,
                pokemons: action.payload
            }
        case 'FILTER_TYPES':
            const allTypes = state.pokemons;
            const statusFilterTypes= action.payload === 'All'? allTypes: allTypes.filter(e=>e.types.includes(action.payload))
            return{
               
                    ...state,
                    pokemons: statusFilterTypes
                

            }    
            
    
            default: return state;
    }

}

export default rootReducer;
