import React from 'react';
import { useEffect,useState } from "react";
import { useDispatch,useSelector } from "react-redux";
import { GetPokemons } from "../actions";
import { Link } from 'react-router-dom';
import Card from './Card';


export default function Home() {
    const dispatch = useDispatch();
    const allPokemons = useSelector ((state)=> state.pokemons)

    useEffect(()=>{ dispatch(GetPokemons());
    },[])

    function handleClick(e){
        e.preventDefault();
        dispatch(GetPokemons)
    }

    function handleSort(e){
        e.preventDefault();

    }
    
    function handleSortHealthScore(e){
        e.preventDefault();
        
    }
    
    function handleFilterStatus(e){
        e.preventDefault();
        
    }

    return(
        <div>
            <Link to='/pokemons'>Crear Pokemon</Link>
            <h1> POKEMON HOME</h1>
            <button onClick={e=>{handleClick(e)}}>volver a cagar todos los Pokemones</button>
            <div>
            <select onChange={e=> handleSort(e)} >
                <option  value='---'>---</option>
                <option  value='A-Z'>A-Z</option>
                <option  value='Z-A'>Z-A</option>
                
            </select>
            <select onChange={e=> handleFilterStatus(e)} >
            <option value='All'>All</option>
            <option value='dairy free'>dairy free</option>
            <option value='fodmap friendly'>fodmap friendly</option>
            <option value='gluten free'>gluten free</option>
            <option value='ketogenic'>ketogenic</option>
            <option value='lacto ovo vegetarianc'>lacto ovo vegetarian</option>
            <option value='paleolithic'>paleolithic</option>
            <option value='pescatarian'>pescatarian</option>
            <option value='primal'>primal</option>
            <option value='vegan'>vegan</option>
            <option value='whole 30'>whole 30</option>
            <option value='paleo'>paleo</option>
            </select>
            <select onChange={e=>handleSortHealthScore(e)}>
                <option value='max'> Max health score </option>
                <option value='min'>Min health score  </option>
                </select>
                {
                all?.map( (e) =>{
                    return( 

                        <fragment>
                    <Link to={"/detail/"+ e.id}>
                    <Card name={e.name} image={e.image} diets={!e.createdInDB? e.diets + '  ' : e.diets.map(e => e.name + (', '))} />
                    </Link>
                    </fragment>

                    )}) 
                    }

            </div>
        </div>

    )
    
}
